  init() {
    if let jsonData = questionsJson.data(using: .utf8) {
      do {
        let questionsArray = try JSONDecoder().decode([Question].self, from: jsonData)
        questions.append(contentsOf: questionsArray)
      } catch {
        print(error)
      }
    }
  }
  
}



extension String {
  
  func htmlDecoded() -> String {
    return replacingOccurrences(of: "&quot;", with: "\"").replacingOccurrences(of: "&#039;", with: "'").replacingOccurrences(of: "&amp;", with: "&").replacingOccurrences(of: "&‌pi;", with: "π")
  }
  
}
